import react,{useState} from 'react';
import './ExpenseForm.css';
const ExpenseForm = () =>{

    const [enterdTitle, setEnderedTitle]=useCase('')
    const [enteredAmount, setEnteredAmount]=useCase('')
    const [enteredDate, setEnteretDate]=useCase('')

    const TitleChangeHandler=(event)=>{
    setEnderedTitle(event.target.value);
};
const amountChangeHandler=(event)=>{
    setEnteredAmount(event.target.value);
};
const dateChangeHandler=(event)=>{
    setEnteretDate(event.target.value);
};
    return <form>
        <div className='new-expense__controls'>
           <div className='new-expense__control'>
                <label>Title</label>
                <input type='text' onChange={TitleChangeHandler} />
        </div>
        <div className='new-expense__control'>
                <label>Amount</label>
                <input type='number' min="0.01" step="0.01" />
        </div>
        <div className='new-expense__control'>
                <label>Date</label>
                <input type='date' min="2019-01-01" max="2022-12-31" />
        </div>
        <div className='new-expense__actions'>
            <button type="submit">Add Expanse</button>
        </div>
        </div>
    </form>
};

export default ExpenseForm;
