import react,{useState} from 'react';
import './ExpenseForm.css';
const ExpenseForm = () =>{

    const [enterdTitle, setEnderedTitle]=useState('')
    const [enteredAmount, setEnteredAmount]=useState('')
    const [enteredDate, setEnteretDate]=useState('')

    const TitleChangeHandler=(event)=>{
    setEnderedTitle(event.target.value);
};
const amountChangeHandler=(event)=>{
    setEnteredAmount(event.target.value);
};
const dateChangeHandler=(event)=>{
    setEnteretDate(event.target.value);
};
const submitHandler=(event) =>{
   event.preventDefault();
   const expanseData={
    title:enterdTitle,
    amount:enteredAmount,
    date:new Date (enteredDate)
   };
   console.log(expanseData);
}

    return <form onClick={submitHandler}>
        <div className='new-expense__controls'>
           <div className='new-expense__control'>
                <label>Title</label>
                <input type='text' onChange={TitleChangeHandler} />
        </div>
        <div className='new-expense__control'>
                <label>Amount</label>
                <input type='number' min="0.01" step="0.01" />
        </div>
        <div className='new-expense__control'>
                <label>Date</label>
                <input type='date' min="2019-01-01" max="2022-12-31" />
        </div>
        <div className='new-expense__actions'>
            <button type="submit">Add Expanse</button>
        </div>
        </div>
    </form>
};

export default ExpenseForm;
